def merge_data(inventory, pricing):
    pricing_map = {item['product_id']: item['price'] for item in pricing}
    merged_list = []
    for item in inventory:
        p_id = item['product_id']
        if p_id in pricing_map:
            merged_list.append({
                'product_id': p_id,
                'name': item['name'],
                'price': pricing_map[p_id]
            })
    return merged_list

def solve_intersection(list_a, list_b):
    set_a = set(list_a)
    set_b = set(list_b)
    intersection = set_a.intersection(set_b)
    return len(intersection)

